import { withIronSessionApiRoute } from 'iron-session/next'
import moment from 'moment'
import { NextApiRequest, NextApiResponse } from 'next'
import nodeHtmlToImage from 'node-html-to-image'

import { getClub, getClubEvents } from '@/api/clubApi'
import { getSmartContract } from '@/api/cryptoApi'
import { getEvent } from '@/api/eventApi'
import { getPublicUser } from '@/api/userApi'
import { doRequest } from '@/lib/Api'
import { getUrlWithSizes } from '@/lib/helpers'
import { sessionOptions } from '@/lib/session'
import { TokenInfo } from '@/model/cryptoModel'

export default withIronSessionApiRoute(async (req: NextApiRequest, res: NextApiResponse) => {
  const { clubSlug, eventId, username, tokenId, invite, dao, web } = req.query

  let name = ''
  let pageName = ''
  let base64Image = ''
  let imageURI = ''
  let hint = ''
  let hintTop = ''

  const puppeteer = {
    args: [
      '--no-sandbox',
      '--disable-setuid-sandbox',
      '--disable-dev-shm-usage',
      '--disable-accelerated-2d-canvas',
      '--no-first-run',
      '--headless',
      '--no-zygote',
      '--disable-gpu',
    ],
    headless: true,
    ignoreHTTPSErrors: true,
  }

  const font =
    'data:application/font-woff2;charset=utf-8;base64,'
  const logo =
    'data:image/svg+xml;base64,'

  if (clubSlug) {
    const [club] = await getClub(Array.isArray(clubSlug) ? clubSlug[0] : clubSlug, { headers: { Authorization: '' } })
    name = club ? club.title : ''
    pageName = 'club'

    if (club?.avatar && !eventId) {
      const b64 = await fetch(getUrlWithSizes(club.avatar, 180, 180), {
        mode: 'no-cors',
      })
        // @ts-ignore
        .then((response) => response.buffer())
        .then((buffer) => buffer.toString('base64'))
        .catch(console.error)
      imageURI = 'data:image/png;base64,' + b64
    }

    if (club?.countParticipants && !eventId) {
      hint = `${club.countParticipants} member${club.countParticipants > 1 ? 's' : ''}`
    }
    if (eventId && club?.id) {
      pageName = 'event'
      const r = await getClubEvents(club.id, { limit: 500 })
      if (!r._cc_errors.length && r.data !== null && r.data?.response?.items) {
        const events = r.data.response.items
        events.forEach((event) => {
          if (event.id === eventId) {
            name = event.title
            hint = `by ${club.title}`
            hintTop = moment(event.date * 1000)
              .format('LT (TTT Z) • ddd • MMM DD')
              .replace('TTT', 'GMT')
          }
        })
      }
    }
  } else if (username) {
    const [user] = await getPublicUser((Array.isArray(username) ? username[0] : username).toLowerCase())
    name = user ? user.name : ''
    pageName = 'profile'

    if (user?.avatar && !eventId) {
      const b64 = await fetch(getUrlWithSizes(user.avatar, 180, 180), {
        mode: 'no-cors',
      })
        // @ts-ignore
        .then((response) => response.buffer())
        .then((buffer) => buffer.toString('base64'))
        .catch(console.error)
      imageURI = 'data:image/png;base64,' + b64
    }

    hint = `@${user?.username}`

    if (invite) {
      name = 'You have been invited to Connect.Club'
      pageName = 'invite'
      hint = user ? `by ${user.name} • @${user?.username}` : ''
    }
  } else if (tokenId) {
    /* Get smart contract */
    const [smartContract, errors] = await getSmartContract(Array.isArray(tokenId) ? tokenId[0] : tokenId)

    if (!errors.length && smartContract) {
      /* Get token info */
      const response = await doRequest<TokenInfo, TokenInfo>({
        method: 'GET',
        endpoint: smartContract.url,
      })
      if (!response._cc_errors.length) {
        pageName = 'membership'

        const tokenInfo = response.data
        name = tokenInfo.name
        hint = `${smartContract.totalSupply} token${smartContract.totalSupply > 1 ? 's were' : 'was'} issued, ${
          smartContract.maxTokenSupply - smartContract.totalSupply
        } remained`

        const b64 = await fetch(tokenInfo.image, { mode: 'no-cors' })
          // @ts-ignore
          .then((response) => response.buffer())
          .then((buffer) => buffer.toString('base64'))
          .catch(console.error)
        imageURI = 'data:image/png;base64,' + b64
      }
    }
  } else if (dao) {
    pageName = 'DAO'
    name = 'Connect.Club DAO'
    hint = `We are building Metaverse together!`
    imageURI =
      'data:image/png;base64,'
  } else if (web) {
    pageName = 'Web'
    hint = `Participate or watch as guest`
    name = 'Connect.Club web version'
    imageURI =
      'data:image/png;base64,'
    if (eventId && !Array.isArray(eventId)) {
      const response = await getEvent(eventId)
      if (!response._cc_errors.length && response.data?.response) {
        const event = response.data?.response
        hint = `${event?.club ? `Club event` : `Event`}${event?.title ? ` : ${event.title}` : ``}`

        if (event?.club) {
          const [club] = await getClub(event.club.slug, { headers: { Authorization: '' } })

          if (club?.avatar) {
            const b64 = await fetch(getUrlWithSizes(club.avatar, 180, 180), {
              mode: 'no-cors',
            })
              // @ts-ignore
              .then((response) => response.buffer())
              .then((buffer) => buffer.toString('base64'))
              .catch(console.error)
            imageURI = 'data:image/png;base64,' + b64
          }
        }
      }
    }
  } else {
    pageName = 'page not found'
  }

  // @ts-ignore
  const images = await nodeHtmlToImage({
    html: `<html>
                    <style>
                        @font-face { font-family: "Proxima"; src: url(${font}) format("woff2"); }
                        *,::after,::before{box-sizing:border-box}@media (prefers-reduced-motion:no-preference){:root{scroll-behavior:smooth}}html{font-size:62.5%}body{margin:0;font-family:system-ui,-apple-system,"Segoe UI",Roboto,"Helvetica Neue",Arial,"Noto Sans","Liberation Sans",sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol","Noto Color Emoji";font-size:1.6rem;font-weight:400;line-height:1.5;color:#000;background-color:#fff;-webkit-text-size-adjust:100%;-webkit-tap-highlight-color:transparent}hr{margin:1.6rem 0;color:inherit;background-color:currentColor;border:0;opacity:.25}hr:not([size]){height:1px}h1,h2,h3,h4,h5,h6{margin-top:0;margin-bottom:.8rem;font-weight:500;line-height:1.2;color:#000}h1{font-size:5.4rem}h2{font-size:2.4rem}h3{font-size:2.1rem}h4{font-size:2.1rem}h5{font-size:1.2rem}h6{font-size:1rem}p{margin-top:0;margin-bottom:1.6rem}abbr[data-bs-original-title],abbr[title]{-webkit-text-decoration:underline dotted;text-decoration:underline dotted;cursor:help;-webkit-text-decoration-skip-ink:none;text-decoration-skip-ink:none}address{margin-bottom:1.6rem;font-style:normal;line-height:inherit}ol,ul{padding-left:3.2rem}dl,ol,ul{margin-top:0;margin-bottom:1.6rem}ol ol,ol ul,ul ol,ul ul{margin-bottom:0}dt{font-weight:700}dd{margin-bottom:.8rem;margin-left:0}blockquote{margin:0 0 1.6rem}b,strong{font-weight:bolder}small{font-size:.875em}mark{padding:.2em;background-color:#fcf8e3}sub,sup{position:relative;font-size:.75em;line-height:0;vertical-align:baseline}sub{bottom:-.25em}sup{top:-.5em}a{color:#4d7dd0;text-decoration:none}a:hover{color:#3b598d}a:not([href]):not([class]),a:not([href]):not([class]):hover{color:inherit;text-decoration:none}code,kbd,pre,samp{font-family:SFMono-Regular,Menlo,Monaco,Consolas,"Liberation Mono","Courier New",monospace;font-size:1em;direction:ltr;unicode-bidi:bidi-override}pre{display:block;margin-top:0;margin-bottom:1.6rem;overflow:auto;font-size:.875em}pre code{font-size:inherit;color:inherit;word-break:normal}code{font-size:.875em;color:#d63384;word-wrap:break-word}a>code{color:inherit}kbd{padding:.32rem .64rem;font-size:.875em;color:#fff;background-color:#212529;border-radius:.32rem}kbd kbd{padding:0;font-size:1em;font-weight:700}figure{margin:0 0 1.6rem}img,svg{vertical-align:middle}table{caption-side:bottom;border-collapse:collapse}caption{padding-top:.8rem;padding-bottom:.8rem;color:#6c757d;text-align:left}th{text-align:inherit;text-align:-webkit-match-parent}tbody,td,tfoot,th,thead,tr{border-color:inherit;border-style:solid;border-width:0}label{display:inline-block}button{border-radius:0}button:focus:not(:focus-visible){outline:0}button,input,optgroup,select,textarea{margin:0;font-family:inherit;font-size:inherit;line-height:inherit}button,select{text-transform:none}[role=button]{cursor:pointer}select{word-wrap:normal}select:disabled{opacity:1}[list]::-webkit-calendar-picker-indicator{display:none}[type=button],[type=reset],[type=submit],button{-webkit-appearance:button}[type=button]:not(:disabled),[type=reset]:not(:disabled),[type=submit]:not(:disabled),button:not(:disabled){cursor:pointer}::-moz-focus-inner{padding:0;border-style:none}textarea{resize:vertical}fieldset{min-width:0;padding:0;margin:0;border:0}legend{float:left;width:100%;padding:0;margin-bottom:.8rem;font-size:calc(2.04rem + .3vw);line-height:inherit}@media (min-width:1200px){legend{font-size:2.4rem}}legend+*{clear:left}::-webkit-datetime-edit-day-field,::-webkit-datetime-edit-fields-wrapper,::-webkit-datetime-edit-hour-field,::-webkit-datetime-edit-minute,::-webkit-datetime-edit-month-field,::-webkit-datetime-edit-text,::-webkit-datetime-edit-year-field{padding:0}::-webkit-inner-spin-button{height:auto}[type=search]{outline-offset:-2px;-webkit-appearance:textfield}::-webkit-search-decoration{-webkit-appearance:none}::-webkit-color-swatch-wrapper{padding:0}::file-selector-button{font:inherit}::-webkit-file-upload-button{font:inherit;-webkit-appearance:button}output{display:inline-block}iframe{border:0}summary{display:list-item;cursor:pointer}progress{vertical-align:baseline}[hidden]{display:none!important}
                        html { width: 1200px; height: 630px }
                        .main { padding: 80px; width: 1200px; height: 630px; position: relative; }
                        body { background: #D9DDE4; font-family: Proxima,serif }
                        .page-name { ${
                          imageURI ? 'margin-left: 230px;' : ''
                        } padding: 7px 20px; background: #4D7DD0; color: #fff; display: inline-flex; border-radius: 36px; font-size: 28px; line-height: 120%; }
                        .content { display: flex; flex-flow: row nowrap; column-gap: 48px; align-items: center; margin-top: 85px; margin-bottom: 100px }
                        .content-text { display: flex; flex-direction: column; row-gap: 20px }
                        .name { font-size: ${name.length > 40 ? '56px' : '64px'}; line-height: 110%; color: #000; }
                        .image { overflow: hidden; border-radius: 50%; width: 180px; height: 180px; flex-shrink: 0 }
                        .img { min-width: 100%; max-width: 100%; min-height: 100%; max-height: 100%; object-fit: cover; }
                        .hint { font-size: 28px; line-height: 120%; color: rgba(0, 0, 0, 0.32); }
                        .hint.top { margin-bottom: 20px }
                        .logo { ${
                          imageURI ? 'margin-left: 230px;' : ''
                        } display: flex; flex-flow: row wrap; column-gap: 13px; align-items: center; font-size: 28px; color: #4D7DD0; }
                    </style>
                    <body>
                        <div class="main">
                            <div class="page-name">{{ pageName }}</div>
                            <div class="content">
                                ${imageURI ? `<div class="image"><img src={{ image }} alt='' class='img'></div>` : ''}
                                <div class='content-text'>
                                    ${hintTop ? `<div class="hint top">{{ hintTop }}</div>` : ``}
                                    <div class='name'>{{ name }}</div>
                                    <div class="hint">{{ hint }}</div>
                                </div>
                            </div>
                            <div class="logo">
                                <img src={{ logo }}>
                                <span>Connect.Club</span>
                            </div>
                        </div>
                    </body>
                </html>`,
    content: [{ name, pageName, image: imageURI, hint, logo, hintTop }],
    puppeteerArgs: puppeteer,
  })
  // @ts-ignore
  base64Image = new Buffer(images[0], 'base64')

  res.writeHead(200, {
    'Content-Type': 'image/png',
    'Content-Length': base64Image.length,
  })
  res.end(base64Image)
}, sessionOptions)
